parameters.text:
=Analyze this sensor telemetry data:

{{ $json.sensor_data || JSON.stringify($json.body) }}

Equipment: {{ $json.equipment_id || $json.body?.equipment_id || 'HYD_PRESS_07' }}
Analysis Type: {{ $json.analysis_type || 'standard' }}

parameters.jsCode:
// Parse the AI response and structure output
const output = $input.first().json.output;

try {
  // Try to extract JSON from the response
  const jsonMatch = output.match(/```json\n([\s\S]*?)\n```/) || output.match(/\{[\s\S]*\}/);
  const parsed = jsonMatch ? JSON.parse(jsonMatch[1] || jsonMatch[0]) : { raw: output };

  return {
    json: {
      ...parsed,
      agent: 'DRISHTI',
      processed_at: new Date().toISOString()
    }
  };
} catch (e) {
  return {
    json: {
      agent: 'DRISHTI',
      status: 'PARSE_ERROR',
      raw_output: output,
      error: e.message,
      processed_at: new Date().toISOString()
    }
  };
}
