// Normalize webhook or subworkflow input to sensor_data, equipment_id, analysis_type
const item = $input.first().json;
const raw = item.body ?? item;
const sensor_data = raw.sensor_data ?? raw;
const equipment_id = raw.equipment_id ?? 'HYD_PRESS_07';
const analysis_type = raw.analysis_type ?? 'standard';
return { json: { sensor_data, equipment_id, analysis_type } };

